export const initialState = {
  basket: [],
  user: null,
};

const reducer = (state, action) => {
  //console.log(action);
  switch (action.type) {
    case "ADD_TO_BASKET":
      return {
        ...state,
        basket: [...state.basket, action.item],
      };
      // eslint-disable-next-line
      break;
    case "REMOVE_FROM_BASKET":
      return state;
      // eslint-disable-next-line
      break;
    default:
      return state;
  }
};

export default reducer;
